components.css:
.map-container {
  max-width: 1200px;
  margin: 40px auto;
  padding: 0 20px;
}

.map-container h2 {
  color: #00733E;
  text-align: center;
  margin-bottom: 20px;
}

.map-wrapper {
  border-radius: 10px;
  overflow: hidden;
  box-shadow: 0 5px 15px rgba(0,0,0,0.1);
}

.map-placeholder {
  background-color: #e9e9e9;
  height: 400px;
  display: flex;
  align-items: center;
  justify-content: center;
  background-image: url('https://via.placeholder.com/1200x400?text=Map+Placeholder');
  background-size: cover;
  background-position: center;
}

.map-content {
  background-color: rgba(255, 255, 255, 0.9);
  padding: 20px 30px;
  border-radius: 5px;
  text-align: center;
}

.map-content p {
  margin-bottom: 10px;
  color: #333;
}

.map-btn {
  background-color: #E0B200;
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  font-weight: 500;
  margin-top: 10px;
  transition: background-color 0.3s;
}

.map-btn:hover {
  background-color: #c99e00;
}

